import pro1 from "../assets/project1.png";
import pro2 from "../assets/project2.png";
import pro3 from "../assets/project3.png";

const WorkCardData = [
  {
    imgsrc: pro1,
    title: "Weather Application",
    text: "Created a React-based single-page application with Django and OpenWeatherMap API delivering accurate, real-time weather information for global locations.",
    view: "https://github.com/CharviBasappa/WeatherApp",
  },
  {
    imgsrc: pro2,
    title: "Sticky Notes",
    text: "Created a sticky notes app for easy organization of tasks, reminders, to-dos and notes.",
    view: "https://github.com/CharviBasappa/StickyNotes",
  },
  {
    imgsrc: pro3,
    title: "Data Visualization",
    text: "Created a customizable interfaces featuring line charts for trend analysis, bar graphs for comparative insights and pie charts for percentage distributions.",
    view: "https://github.com/CharviBasappa/Data_Visualization_Project",
  },
];

export default WorkCardData;
